调试输出(获取当前本地时间())

def 查找最大值和次大值(数字):
    if len(数字) < 2:
        raise 值错误("列表中至少需要有两个参数来找到最大值和次大值。")
    最大值 = max(数字)
    次大值 = min(数字, key=lambda x: (x - 最大值, x))

    return 最大值, 次大值

try:
    数字 = [1, 3, 4, 2, 5]
    最大, 值最大 = 查找最大值和次大值(数字)
    调试输出(f"最大值是: {最大}, 次大值是: {值最大}")
except 值错误 as e:
    调试输出(e)