def add(num1, num2):
    """
    Performs addition of the given two numbers
    """
    return num1 + num2

def subtract(num1, num2):
    """
    Performs subtraction of the given two numbers
    """
    return num1 - num2

def multiply(num1, num2):
    """
    Performs multiplication of the given two numbers
    """
    return num1 * num2

def divide(num1, num2):
    """
    Performs division of the given two numbers
    """
    return num1 / num2

if __name__ == '__main__':
    print(add(7,2))
    print(subtract(9,3))
    print(multiply(4,8))
    print(divide(5,2))

